from typing import Optional

from dna_common.config import BaseConfig


class AppBaseConfig(BaseConfig):

    name: str = 'app'
    version: Optional[str] = None

    inspector: bool = True
    inspector_host: str = '0.0.0.0'
    inspector_port: int = 9300
    inspector_socket: Optional[str] = None
    inspector_status_healthy: str = 'healthy'
    inspector_status_unhealthy: str = 'sick'

    @property
    def app_name(self):
        return self.name

    @property
    def app_version(self):
        return self.version
